package test1;

public class Typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 int num=20;
 float num1=num;
 {
	 System.out.println("the float value is:"+num1);
 }
 //Implicit
 
 double a=20.00;
 int b= (int)a;
 {
	 System.out.println("the int value is:"+b);
 }
	}//Explicit

}
